DROP TABLE MTK_EQUIPMENT_CF_TAB;

CREATE TABLE MTK_EQUIPMENT_CF_TAB
(
   CONTRACT		   		VARCHAR2(5)        NOT NULL,
   MCH_CODE        		VARCHAR2(100)        NOT NULL,
   ALT_DESCRIPTION 		VARCHAR2(100) NULL,
   OLD_ASSET	   		VARCHAR2(100) NULL,
   EQUIPMENT_SERIAL_MFG VARCHAR2(100) NULL,
);

alter session set NLS_NUMERIC_CHARACTERS = '.,';

SELECT 'INSERT INTO MTK_EQUIPMENT_CF_TAB (CONTRACT,MCH_CODE,ALT_DESCRIPTION,OLD_ASSET,EQUIPMENT_SERIAL_MFG) VALUES (''' 
+ [CONTRACT] 
+ ''',''' + [MCH_CODE] 
+ ''',''' + isnull([ALT_DESCRIPTION],'')
+ ''',''' + isnull([OLD_ASSET],'')
+ ''',''' + isnull([EQUIPMENT SERIAL MFG],'')
+  ''');'   aa
FROM ( SELECT [CONTRACT]
      ,[MCH_CODE]      
      ,replace([ALT_DESCRIPTION], '''', '') as [ALT_DESCRIPTION]
      ,replace([OLD_ASSET], '''', '') as [OLD_ASSET]
      ,replace([EQUIPMENT SERIAL MFG], '''', '') as [EQUIPMENT SERIAL MFG]
  FROM [IFSDEV-ManualUpload].[dbo].[EquipmentObject$]) aaa   


DECLARE   
   CURSOR get_qaman IS
      SELECT   a.objid,            
			 a.objversion,
			 b.ALT_DESCRIPTION,
			 b.EQUIPMENT_SERIAL_MFG,
			 b.OLD_ASSET
	FROM  EQUIPMENT_OBJECT a
	INNER JOIN MTK_EQUIPMENT_CF_TAB b ON b.CONTRACT = a.CONTRACT 
	AND b.MCH_CODE = a.MCH_CODE;

   info_                VARCHAR2(32000);
   objid_               VARCHAR2(32000);
   objversion_          VARCHAR2(32000);
   attr_                VARCHAR2(26000);   
   attr_cf_    			VARCHAR2(26000); 
   
BEGIN
   
   FOR row_ IN get_qaman LOOP
      objid_ := row_.objid;
      objversion_ := row_.objversion;
      
      Client_SYS.Clear_Attr(attr_);
      Client_SYS.Clear_Attr(attr_cf_);
      Client_SYS.Add_To_Attr('CF$_AIM_ALT_DESCRIPTION', row_.ALT_DESCRIPTION, attr_cf_);      
      Client_SYS.Add_To_Attr('CF$_AIM_EQUIP_SERIAL', row_.EQUIPMENT_SERIAL_MFG, attr_cf_);      
      Client_SYS.Add_To_Attr('CF$_AIM_OLD_ASSET', row_.OLD_ASSET, attr_cf_);      
	  
      EQUIPMENT_SERIAL_CFP.Cf_Modify__(info_, objid_, attr_cf_, attr_, 'DO');
      --COMMIT;
   END LOOP;   
END;

