DROP TABLE MTK_INPUT_UNIT_MEAS_CF_TAB;

CREATE TABLE MTK_INPUT_UNIT_MEAS_CF_TAB
(
   INPUT_UNIT_MEAS_GROUP_ID           VARCHAR2(30)        NOT NULL,
   UNIT_CODE                          VARCHAR2(30)        NOT NULL,
   CONVERSION_FACTOR                  NUMBER              NOT NULL
);

alter session set NLS_NUMERIC_CHARACTERS = '.,';

SELECT 'INSERT INTO MTK_INPUT_UNIT_MEAS_CF_TAB (INPUT_UNIT_MEAS_GROUP_ID,UNIT_CODE,CONVERSION_FACTOR) VALUES (''' 
+ INPUT_UNIT_MEAS_GROUP_ID 
+ ''',''' + UNIT_CODE 
+ ''',''' + CONVERSION_FACTOR 
+  ''');'   aa
FROM ( SELECT  [Input UoM Group ID]	INPUT_UNIT_MEAS_GROUP_ID  
	       ,[Input UoM]	UNIT_CODE
	       ,[Input UoM Conv Factor]	CONVERSION_FACTOR
  FROM [IFSDEV-ManualUpload].[dbo].[NEW_CONV_FACT$]) aaa  

DECLARE   
   CURSOR get_qaman IS
      SELECT   a.objid,            
			 a.objversion,
			 b.CONVERSION_FACTOR
	FROM  INPUT_UNIT_MEAS a
	INNER JOIN MTK_INPUT_UNIT_MEAS_CF_TAB b ON b.INPUT_UNIT_MEAS_GROUP_ID = a.INPUT_UNIT_MEAS_GROUP_ID 
	AND b.UNIT_CODE = a.UNIT_CODE;

   info_                VARCHAR2(32000);
   objid_               VARCHAR2(32000);
   objversion_          VARCHAR2(32000);
   attr_                VARCHAR2(26000);   
   attr_cf_    			VARCHAR2(26000); 
   
BEGIN
   
   FOR row_ IN get_qaman LOOP
      objid_ := row_.objid;
      objversion_ := row_.objversion;
      
      Client_SYS.Clear_Attr(attr_);
      Client_SYS.Add_To_Attr('CONVERSION_FACTOR', row_.CONVERSION_FACTOR, attr_);      

      Client_SYS.Clear_Attr(attr_cf_);
      Client_SYS.Add_To_Attr('CF$_AIM_MULTIPLY_DB', 'TRUE', attr_cf_);      
	  
      INPUT_UNIT_MEAS_CFP.MODIFY__(info_, objid_, objversion_, attr_cf_, attr_, 'DO');
      --COMMIT;
   END LOOP;   
END;